    if (stock == null) stock = 0;

    if (nombre == null || sku == null || nombre_servicio == null || part_number == null || stock_min == null || unidad == null) {
        return res.status(400).json({ msg: 'Bad Request. Please Fill all Fields' });
    }

    console.log(req.body);

    try {
